import matplotlib.pyplot as plt
import os.path
import numpy as np
import PIL
import PIL.Image
import PIL.ImageDraw

 
#Open Charmander Wallpaper picture in numpy
directory = os.path.dirname(os.path.abspath(__file__))  
filepath_CharmanderW = os.path.join(directory, 'CharmanderWallpaper.jpg')
CharmanderWallpaper_numpy = plt.imread(filepath_CharmanderW)

#Open Nero in numpy
filepath_Nero = os.path.join(directory, 'Nero.Fate.Extella.JPG')
Nero_numpy = plt.imread(filepath_Nero)

# Open Charmander picture in numpy.
filepath_charmander = os.path.join(directory, 'Charmander.JPG')
Charmander_numpy = plt.imread(filepath_charmander)

# Opens the fire border in numpy.
filepath_FireB = os.path.join(directory, 'FireBorder.jpg')
FireBorder_numpy = plt.imread(filepath_FireB)

# Converts all images to PIL
CharmanderW_image_pil = PIL.Image.fromarray(CharmanderWallpaper_numpy)
Nero_image_pil = PIL.Image.fromarray(Nero_numpy)
Charmander_image_pil = PIL.Image.fromarray(Charmander_numpy)
FireB_image_pil = PIL.Image.fromarray(FireBorder_numpy)


# Resizews Nero Charmander and flame border to fit
Nero_img_large = Nero_image_pil.resize((450,600))
Charmander_img_large = Charmander_image_pil.resize((500,500))
FireB_img_large = FireB_image_pil.resize((1679,199))

# Places Nero and Charmander on Wallpaper
CharmanderW_image_pil.paste(Nero_img_large,(19,246))
CharmanderW_image_pil.paste(Charmander_img_large,(1174,246))

# Places fire border on bottom.
CharmanderW_image_pil.paste(FireB_img_large,(0,850))
CharmanderW_numpy = np.array(CharmanderW_image_pil)
Nero_numpy = np.array(Nero_img_large)
Charmander_numpy = np.array(Charmander_img_large)
FlamesBorder_numpy = np.array(FireB_img_large)
# Makes the picture grayscale
gray_CharmanderW_pil = CharmanderW_image_pil.convert('LA')

# Converts picture back to numpy.
gray_CharmanderW_numpy = np.array(gray_CharmanderW_pil)


fig, ax = plt.subplots(1, 1)
ax.imshow(CharmanderW_numpy, interpolation='none')
fig.show()